mapper.select:
SELECT * FROM (
SELECT ROWNUM RN, T.* FROM (
SELECT P.*, H.EMP_NAME, B.BOARD_NAME FROM POST P INNER JOIN BOARD B ON P.BOARD_NO = B.BOARD_NO INNER JOIN HR H ON P.EMP_NO = H.EMP_NO WHERE P.BOARD_NO = #{boardNo} AND P.IS_DELETED = 'N' ORDER BY P.POST_NO
) T ) WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE POST
SET
	TITLE = #{title},
	CATEGORY = #{category},
	CONTENT = #{content}
WHERE POST_NO = #{postNo}
